package com.haier.uplus.control.dialog;import android.app.Dialog;import android.content.Context;import android.view.View;import android.widget.TextView;import com.haier.uplus.control.R;public class SmartConfigDialog extends Dialog {	private Context mContext;	private TextView button;	public SmartConfigDialog(Context context) {		super(context, R.style.notitle_dialog);		mContext = context;		this.setContentView(R.layout.dialog_smart_link_config);		button = findViewById(R.id.btn_cancel_text);	}	public void setOnCancelListener(View.OnClickListener listener) {		button.setOnClickListener(listener);	}	public SmartConfigDialog(Context context, int resLayoutId) {		super(context, resLayoutId);		mContext = context;		this.setContentView(resLayoutId);		button = findViewById(R.id.btn_cancel_text);	}}